function calculatePeerComparison(employee) {
  const avgScore =
    (employee.overall_score +
      employee.teamwork_score +
      employee.communication_score +
      employee.skillset_score) /
    4;

  let peerComparisonScore = 0;
  let peerComparisonType = "";

  if (avgScore >= 8) {
    peerComparisonScore = 9;
    peerComparisonType = "Great Employee";
  } else if (avgScore >= 6) {
    peerComparisonScore = 7;
    peerComparisonType = "Average Employee";
  } else {
    peerComparisonScore = 5;
    peerComparisonType = "Below Average Employee";
  }

  return {
    id: employee.id,
    name: employee.name,
    peerComparisonScore,
    peerComparisonType,
  };
}

const result = calculatePeerComparison(components.table1.selectedRow);
return result;
